import React from 'react';
import { useParams } from 'react-router-dom';

const blogs = [
  {
    id: 1,
    name: 'Tourism in Petra',
    author: 'Nidal',
    content: 'Content 1',
    imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/3/30/Petra_%2C_Al-Khazneh_2.jpg/2238px-Petra_%2C_Al-Khazneh_2.jpg', // Replace with your image URL
  },

];

function BlogDetails() {
    const { id } = useParams();
    const blog = blogs.find((blog) => blog.id === parseInt(id, 10));
  
    if (!blog) {
      return <div>Blog not found</div>;
    }
  
    return (
      <div className="blog-details">
        <h2>{blog.title}</h2>
        <p>Author: {blog.author}</p>
        <img src={blog.imageUrl} alt={blog.title} />
        <p>{blog.description}</p>
      </div>
    );
  }
  
  export default BlogDetails;
