import React, { useState } from 'react';
import Axios from 'axios';
import './App.css'; // استيراد ملف الأنماط الخاص بالتصميم

function BlogForm() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [author, setAuthor] = useState('');
  const [image, setImage] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('description', description);
      formData.append('author', author);
      formData.append('image', image);

      const response = await Axios.post('/api/createBlog', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.status === 200) {
        setSuccessMessage('Blog submitted successfully.');
        // مسح النموذج
        setTitle('');
        setDescription('');
        setAuthor('');
        setImage(null);
      }
    } catch (error) {
      setErrorMessage('Error submitting the blog. Please try again.');
    }
  };

  return (

        <div className="bg-white shadow p-4 py-8">
          <div className="heading text-center font-bold text-2xl m-5 text-gray-800 bg-white">New Post</div>
          <div className="editor mx-auto w-10/12 flex flex-col text-gray-800 border border-gray-300 p-4 shadow-lg max-w-2xl">
            <input
              className="title bg-gray-100 border border-gray-300 p-2 mb-4 outline-none"
              spellCheck="false"
              placeholder="Title"
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <input
              className="title bg-gray-100 border border-gray-300 p-2 mb-4 outline-none"
              spellCheck="false"
              placeholder="Author"
              type="text"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
            />
            <textarea
              className="description bg-gray-100 sec p-3 h-60 border border-gray-300 outline-none"
              spellCheck="false"
              placeholder="Describe everything about this post here"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
    
            <div className="icons flex text-gray-500 m-2">
              <label htmlFor="select-image">
                <svg className="mr-2 cursor-pointer hover:text-gray-700 border rounded-full p-1 h-7" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13" />
                </svg>
                <input
                  hidden
                  type="file"
                  multiple
                  onChange={handleImageUpload}
                />
              </label>
              <div className="count ml-auto text-gray-400 text-xs font-semibold">0/300</div>
            </div>
    
            <div id="preview" className="my-4 flex">
              {images.map((image, index) => (
                <div key={index} className="relative w-32 h-32 object-cover rounded">
                  {image.preview ? (
                    <>
                      <img src={image.url} className="w-32 h-32 object-cover rounded" />
                      <button onClick={() => setImages(images.filter((_, i) => i !== index))} className="w-6 h-6 absolute text-center flex items-center top-0 right-0 m-2 text-white text-lg bg-red-500 hover:text-red-700 hover:bg-gray-100 rounded-full p-1"><span className="mx-auto">×</span></button>
                      <div className="text-xs text-center p-2">{image.size}</div>
                    </>
                  ) : (
                    <>
                      <svg className="fill-current w-32 h-32 ml-auto pt-1" xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
                        <path d="M15 2v5h5v15h-16v-20h11zm1-2h-14v24h20v-18l-6-6z" />
                      </svg>
                      <button onClick={() => setImages(images.filter((_, i) => i !== index))} className="w-6 h-6 absolute text-center flex items-center top-0 right-0 m-2 text-white text-lg bg-red-500 hover:text-red-700 hover-bg-gray-100 rounded-full p-1"><span className="mx-auto">×</span></button>
                      <div className="text-xs text-center p-2">{image.size}</div>
                    </>
                  )}
                </div>
              ))}
            </div>
            {/* Buttons */}
            <div className="buttons flex justify-end">
              <div className="btn border border-indigo-500 p-1 px-4 font-semibold cursor-pointer text-gray-200 ml-2 bg-indigo-500">Post</div>
            </div>
          </div>
        </div>
      );
    }
    


export default BlogForm;
