import React from 'react';
import { useParams } from 'react-router-dom';

const BlogDetails = ({ blogs }) => {
  const { id } = useParams();
  const selectedBlog = blogs.find(blog => blog.id === parseInt(id, 2));


  if (!selectedBlog) {
    return <div>Blog not found.</div>;
  }


    return (
        <div>
          <h2>{selectedBlog.name}</h2>
          <p>Author: {selectedBlog.author}</p>
          <p>Published Date: {selectedBlog.publishedDate}</p>
          <img src={selectedBlog.imageUrl} alt={selectedBlog.name} />
          <p>{selectedBlog.content}</p>
          {/* يمكنك عرض المزيد من التفاصيل هنا */}
        </div>
      );

};

export default BlogDetails;
